example.screen:
#!/bin/bash
cat /etc/passwd | while read l; do uid=`echo $l | cut -d : -f 1` uidNumber=`echo $l | cut -d : -f 3` gidNumber=`echo $1 | cut -d : -f 4` gecos=`echo $l | cut -d : -f 5` homeDirectory=`echo $l | cut -d : -f 6` loginShell=`echo $l | cut -d : -f 6` userPassword=`cat /etc/shadow | grep $uid | cut -d : -f 2`
echo "dn: cn=$gecos,ou=people,dc=mycompany,dc=com" echo "objectClass: account" echo "objectClass: posixAccount" echo "cn: $gecos" echo "uid: $uid" echo "uidNumber: $uidNumber" echo "gidNumber: $gidNumber" echo "homeDirectory: $homeDirectory" echo "loginShell: $loginShell" echo "userPassword: $userPassword" done
