svg.script:
var pointLoc = 0;
var vWidth = 700;
var vHeight = 200;
var crossH = 0;
var crossV = 0;
var maxV = 0;
var maxL = 0;
var advX = 1;
var interval = 0;
var xpstVis = 1;
var ignoreNext = 0;
var eraseL = 0;
var samp = 1;
var X_array = 0;

rxData = [top.curr_rxData];

function initial(){
	xunit = [];
	xunitfq = [];
	bpc_unit = [];

	gen_svg();

	TextTX = E('TextTX');
	setText(TextTX, "─ Transmission");
	TextRX = E('TextRX');
	setText(TextRX, "─ Reception");
	var i = 0;	
	for(var y=7; y>=0; y--){
		bpc_unit[y] = E('bpc_unit' + y);
		setText(bpc_unit[y], 2*i);
		i++;
	}
}

function gen_svg(){
	polyBPC_TX = E('polyBPC_TX');
	polyBPC_RX = E('polyBPC_RX');
	updateSVG();
}

function setText(e, text){
	if(e.firstChild)
		e.removeChild(e.firstChild);
	e.appendChild(document.createTextNode(text));
}

function E(id){
	return document.getElementById(id);

}

function drawData_BPC(poly, data, mode, color){		
	var i;
	var pt;
	var x, y;
	var d;

	if (data.length == 0) return;

	maxV = 14;
	maxL = data.length;
	advX = vWidth/maxL;	//X Spacing

	if (mode == 0) {
		poly.setAttribute('fill-opacity', '0.5');
		pt = '0,' + vHeight;
	}
	else {
		poly.setAttribute('fill-opacity', '0');
		pt = '';
	}	

	poly.setAttribute('stroke', color);
	poly.setAttribute('fill', color);

	x = 0;	
	for (i = 0; i < data.length; ++i) {
		if (i < 0) {
			d = 0;
		}
		else {
			d = Math.round(data[i]*175/14);						
		}

		y = vHeight - d;
		pt += ' ' + Math.round(x) + ',' + y;
		x += advX;
	}

	pt += ' ' + vWidth + ',' + vHeight;	
	poly.setAttribute('points', pt);
}

function updateSVG(){
	if(top.bpc_us){
		if(top.bpc_us.length == 256){
			X_array = 32;
			E('xunit2').setAttribute('x', "22%");	//Tune position: 64
			E('xunit3').setAttribute('x', "34.5%");	////Tune position: 96
			E('xunitfq2').setAttribute('x', "46%");	////Tune position: 552
		}else if(top.bpc_us.length == 512)
			X_array = 64;
		else if(top.bpc_us.length == 4096)
			X_array = 512;
		else
			X_array = 0;

		if(X_array != 0){	
			for(var x=1; x<9; x++){
				xunit[x] = E('xunit' + x);
				setText(xunit[x], X_array*x);
			}		
			for(var y=1; y<5; y++){
				xunitfq[y] = E('xunitfq' + y);
				setText(xunitfq[y], X_array*2*y*4.3125);
			}	
		}

		drawData_BPC(polyBPC_TX, top.bpc_us, 0, "#B232B2");
		drawData_BPC(polyBPC_RX, top.bpc_ds, 0, "#33CCFF");
	}	
	setTimeout('gen_svg();', 5000);
}

function setText(e, text){
	if(e.firstChild)
		e.removeChild(e.firstChild);
	e.appendChild(document.createTextNode(text));
}
